import React, {useState} from 'react'
import Box from '@mui/material/Box'
import TextField from '@mui/material/TextField'
import { Button } from '@mui/material'

const RegistrarPilotos = () => {

    const [datos, setDatos] = useState({
        Codigo:'',
        Nombre:'',
        HorasVuelo:''
    })

    const handleChange = (event) => {
        setDatos({
            ...datos,
            [event.target.name]:event.target.value
        })
    }

    const handleSubmit = async(e) => {
        e.preventDefault();
        console.log("Codigo miembro: " + datos.Codigo + " Nombre: " + datos.Nombre + " Nombre miembro: " + datos.HorasVuelo);
    }

  return (
    <>
    <form onSubmit={handleSubmit}>
        <Box
            component="form"
            sx={{
                '& > :not(style)': { m: 1, width: '100%' },
            }}
            noValidate
            autoComplete="off"
        >
            <TextField 
                required
                name='Codigo'
                id="filled-basic" 
                label="CÃ³digo del miembro" 
                variant="filled" 
                onChange={handleChange}
                type='number'
            />
            <TextField 
                required
                name='Nombre'
                id="filled-basic" 
                label="Nombre" 
                variant="filled" 
                onChange={handleChange}
                type='text'
            />  
            <TextField 
                required
                name='HorasVuelo'
                id="filled-basic" 
                label="Nombre de los miembros" 
                variant="filled" 
                onChange={handleChange}
                type='text'
            />      
        </Box>
        <Button type='submit' className='button' variant='contained'>enviar</Button>
    </form>
    </>
  )
}

export default RegistrarPilotos
